var addState = function(value, position){
    var state = $("<div>").attr({
        id: "state"+position
    });
    var stateDel = $("<span>Delete</span>").attr({
        display:'inline',
        position:position
    })
    stateDel.click(function(event){
        var pos = event.target.getAttribute("position");
        var label ="state"+pos
        $("#"+label).remove();
    });

    var stateInput = $('<input>').attr({
        type: 'text',
        id: 'stateInput'+position,
        value: value,
        class:'form-control',
    });
    stateInput.css("width","75%");
    stateInput.css("display","inline");
    state.append(stateInput);
    state.append(stateDel);
    $("#FeatureStates").append(state)


}
$("#FeatureStates").empty();

var states = JSON.parse(this.value);
for(var i=0;i<states.length;i++){
    addState(states[i], i);
}
if (!window.hasSubscribedtoAddButton){
    window.hasSubscribedtoAddButton = true;
    $("#AddButton").click(function(event){

        addState();
    });   
}

