var summarizeNodesForSave = function(){
    var nodesSave = [];
    var networkNodes = $("#NetworkNodes");
    for (var i=0; i<networkNodes.children().length; i++){
        var networkNode = $(networkNodes.children()[i]);
        var nodeSave ={
            "name": "",
            "probabilities_present": [],
            "probabilities_absent": []
        };
        var selectInput = networkNode.find("#featureSelect");//[i];
        nodeSave.name=selectInput.val();
        var presentProbs = networkNode.find(".presentProbInput");
        var absentProbs = networkNode.find(".absentProbInput");
        for (var j=0;j<presentProbs.length;j++){
            nodeSave.probabilities_present.push(presentProbs[j].value);
            nodeSave.probabilities_absent.push(absentProbs[j].value);
        }
        nodesSave.push(nodeSave);
    }
    // alert(JSON.stringify(nodesSave));
    return nodesSave;
}

